import { StatusBar } from "expo-status-bar";
import React from "react";
//Se importan todos los elementos que sean controles
import { StyleSheet, Text, View, TextInput, Button } from "react-native";

export default function App() {
  //Definir variables de estado (componente):
  cont
  return (
    <View style={styles.container}>
      <Text>Mi Calculadora</Text>
      <Text>{"\n"}</Text>
      <Text>Valor 1</Text>
      <TextInput
        placeholder="Ingrese valor 1"
        style={{ borderBottomWidth: 2, color: "blue", backgroundColor: "aqua" }}
      />
      <Text>{"\n"}</Text>
      <Text>Valor 2</Text>
      <TextInput
        placeholder="Ingrese valor 2"
        style={{ borderBottomWidth: 2, color: "blue", backgroundColor: "aqua" }}
      />
      <Text>{"\n"}</Text>
      <Text>Resultado</Text>
      <TextInput
        style={{ borderBottomWidth: 2, color: "blue", backgroundColor: "aqua" }}
      />
      <Text>{"\n"}</Text>
      <Button 
      title="Sumar"
      />
       <Text>{"\n"}</Text>
      <Button 
      title="Restar"
      />
       <Text>{"\n"}</Text>
      <Button 
      title="Multiplicar"
      />
       <Text>{"\n"}</Text>
      <Button 
      title="Dividir"
      />
       <Text>{"\n"}</Text>
      <Button 
      title="Limpiar"
      />

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
});
