import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View } from 'react-native';

export default function App() {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Agencia de Turismo</Text>
      <View>
        <Text>Idendi</Text>
        <TextInput
          style={styles.inputs}
          onChangeText={setValorPrestamo}
          value={valorPrestamo}
        ></TextInput>

        <Text>Tipo del prestamo</Text>
        <TextInput
          style={styles.inputs}
          onChangeText={setTipoPrestamo}
          value={tipoPrestamo}
        ></TextInput>

        <Text>NÃºmero de Cuotas</Text>
        <TextInput
          style={styles.inputs}
          onChangeText={setNumeroCuotas}
          value={numeroCuotas}
        ></TextInput>

        <Text>Valor cuota</Text>
        <TextInput style={styles.inputs} value={cuota}></TextInput>

        <Text>Total Deuda:</Text>
        <TextInput style={styles.inputs} value={deuda}></TextInput>
      </View>
      <View style={styles.footer}>
        <Button
          style={styles.button}
          title="Calcular"
          onPress={Calcular}
        ></Button>
        <Text>{"\n"}</Text>
        <Button style={styles.button} title="Limpiar" onPress={Limpiar}>
          Limpiar
        </Button>
      </View>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },

  title: {
    fontSize: 60,
    fontWeight: 10,
    fontFamily: "Neucha",
    color: "red",
  },

  inputs: {
    border: "2px solid #000",
    borderRadius: 5,
    marginBottom: 15,
    color: "blue",
    height: 40,
  },

  button: {
    height: 50,
    width: 20,
    marginTop: 20,
  },

  footer: {
    marginTop: 40,
    display: "block",
  },

  error: {
    color: "red",
  },
});
