    { key: 'report', title: 'Informes', icon: 'history' },
    { key: 'money', title: 'Transferencias', icon: 'history' },
    { key: 'supplier', title: 'Proveedores', icon: 'queue-music' },
  ])

  return (
   <BottomNavigation>
     navigationState={{ index, routes }} //
      onIndexChange={setIndex}
      renderScene={renderScene}
   </BottomNavigation>
  );
}

