import React from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  Picker,
  Switch,
  CheckBox,
  ImageBackground,
} from "react-native";

export default function App() {
  return (
    <View style={styles.container}>
      {/* <View style={{ flexDirection: "row" }}> Poner imagen en fila */}
      {/* FlexWrap: Es para que sea responsive. Lo que no quepa a la derecha, baje a la siguiente linea. */}
      <View style={{ flexDirection: "row", flexWrap: "wrap" }}>
        {" "}
        <Image
          source={require("./images/Futbol_1.jpg")}
          style={{
            width: 150,
            height: 150,
            borderRadius: 10,
            borderColor: "black",
            borderWidth: 2,
            resizeMode: "stretch",
          }}
        />
        <Image
          source={require("./images/Futbol_2.jpg")}
          style={{
            width: 150,
            height: 150,
            borderRadius: 10,
            borderColor: "black",
            borderWidth: 2,
            resizeMode: "stretch",
            marginLeft:5
          }}
        />
        <Image
          source={{uri : 'https://elcomercio.pe/resizer/4SD6VXBY-0Iq8UlNfBle_fqZtQU=/580x330/smart/filters:format(jpeg):quality(75)/cloudfront-us-east-1.images.arcpublishing.com/elcomercio/74XZP4BKTFGWFDLHSMXOPBHGIE.jpg'}}
          style={{
            width: 100,
            height: 100,
            borderRadius: 10,
            borderColor: "black",
            borderWidth: 2,
            // resizeMode: "stretch",
            marginLeft: 5
          }}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
});
