import { StatusBar } from "expo-status-bar";
import React, { useState } from "react";
//Se importan todos los elementos que sean controles
import { StyleSheet, Text, View, TextInput, Button } from "react-native";

export default function App() {
  //Definir variables de estado (componente):
  const [valorPrestamo, setValorPrestamo] = useState("");
  const [numeroCuotas, setNumeroCuotas] = useState("")
  const [resultado, setResultado] = useState("");

  const calcularResultado = (operador) => {
    if (valorPrestamo > 4000000 && valorPrestamo <= 100000000) {
      let mresultado = 0;
      switch (operador) {
        case "Vivienda":
          mresultado = parseFloat(valorPrestamo) * 0.5;
          break;
        case "Educacion":
          mresultado = parseFloat(valorPrestamo) * 0.8;
        case "c":
          mresultado = 0;
          setValorPrestamo("");
          break;
      }
      setResultado(mresultado);
    } else alert("Debe ingresar valor del prestamo");
  };

  return (
    <View style={styles.container}>
      <Text>Simulador de Crédito</Text>
      <Text>{"\n"}</Text>
      <Text>Valor del prestamo</Text>
      <TextInput
        placeholder="Ingrese valor solicitado"
        style={{
          borderBottomWidth: 2,
          color: "aqua",
          backgroundColor: "aqua",
        }}
        onChangeText={(valorPrestamo) => setValor1(valorPrestamo)}
        value={valorPrestamo}
      />
      <Text>{"\n"}</Text>
      <Text>Valor Cuota</Text>
      <TextInput
        style={{ borderBottomWidth: 2, color: "blue", backgroundColor: "aqua" }}
        value={resultado}
      />
      <Text>{"\n"}</Text>
      <Text>Total Deuda</Text>
      <TextInput
        style={{ borderBottomWidth: 2, color: "blue", backgroundColor: "aqua" }}
        value={resultado}
      />
      <Text>{"\n"}</Text>
      <Button title="Vivienda" onPress={() => calcularResultado("Vivienda")} />
      <Text>{"\n"}</Text>
      <Button
        title="Educacion"
        onPress={() => calcularResultado("Educacion")}
      />
      <Text>{"\n"}</Text>
      <Button title="Limpiar" onPress={() => calcularResultado("c")} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
});
