import { StatusBar } from "expo-status-bar";
import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  TextInput,
  View,
  Button,
  Image,
  Picker,
  Switch,
  CheckBox,
  ImageBackground,
} from "react-native";

export default function App() {
  const [valorPrestamo, setValorPrestamo] = useState();
  const [tipoPrestamo, setTipoPrestamo] = useState("");
  console.log(tipoPrestamo)
  const [numeroCuotas, setNumeroCuotas] = useState();
  const [cuota, setCuota] = useState();
  const [deuda, setDeuda] = useState();

  let dinero = parseInt(valorPrestamo);
  let numero_Cuotas = parseInt(numeroCuotas);

  const prueba =(e) => {
console.lo
  }

  const Calcular = () => {
    if (dinero < 4000000 || dinero > 100000000) {
      alert("El valor del pr√©stamo debe ser entre 4.000.000 y 100.000.000");
    } else 
    if (numero_Cuotas < 12 || numero_Cuotas > 60) {
      alert("El numero de cuotas debe de estar entre 12 y 60");
    } else 
    if (tipoPrestamo > 4 || tipoPrestamo < 1) {
      alert("Escoger el tipo de prestamo");
    } else {
      switch (tipoPrestamo) {
        case "Vivienda":
          setDeuda(dinero * 0.005 * numero_Cuotas + dinero);
          setCuota((dinero * 0.005 * numero_Cuotas + dinero) / numero_Cuotas);
          break;

        case "Educacion":
          setDeuda(dinero * 0.008 * numero_Cuotas + dinero);
          setCuota((dinero * 0.008 * numero_Cuotas + dinero) / numero_Cuotas);
          break;

        case "Automovil":
          setDeuda(dinero * 0.015 * numero_Cuotas + dinero);
          setCuota((dinero * 0.015 * numero_Cuotas + dinero) / numero_Cuotas);
          break;

        case "Otro":
          setDeuda(dinero * 0.02 * numero_Cuotas + dinero);
          setCuota((dinero * 0.02 * numero_Cuotas + dinero) / numero_Cuotas);
          break;
      }
    }
  };

  const Limpiar = () => {
    setValorPrestamo("");
    setTipoPrestamo("");
    setNumeroCuotas("");
    setCuota("");
    setDeuda("");
  };

  return (
    <View style={styles.container}>
      <Image
        source={require("./img/bank.jpg")}
        style={{
          width: 150,
          height: 150,
          borderRadius: 10,
          borderColor: "black",
          borderWidth: 2,
          //resizeMode: "stretch",
        }}
      />

      <Text style={styles.title}> Simula tu Cr√©dito</Text>
      <View>
        <Text>Valor del prestamo</Text>
        <TextInput
          style={styles.inputs}
          onChangeText={setValorPrestamo}
          value={valorPrestamo}
        ></TextInput>

        <Text>Tipo del prestamo</Text>
        <Picker
          style={styles.inputs}
          onChangeText={prueba()}
          value={tipoPrestamo}
        >
          <Picker.Item label="Vivienda" value="Vivienda" />
          <Picker.Item label="Educaci√≥n" value="Educacion" />
          <Picker.Item label="Autom√≥vil" value="Automovil" />
          <Picker.Item label="Otro" value="Otro" />
           </Picker>

        <Text>N√∫mero de Cuotas</Text>
        <TextInput
          style={styles.inputs}
          onChangeText={setNumeroCuotas}
          value={numeroCuotas}
        ></TextInput>

        <Text>Valor cuota</Text>
        <TextInput style={styles.inputs} value={cuota}></TextInput>

        <Text>Total Deuda:</Text>
        <TextInput style={styles.inputs} value={deuda}></TextInput>
      </View>
      <View style={styles.footer}>
        <Button
          style={styles.button}
          title="Calcular"
          onPress={Calcular}
        ></Button>
        <Text>{"\n"}</Text>
        <Button style={styles.button} title="Limpiar" onPress={Limpiar}>
          Limpiar
        </Button>
      </View>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },

  title: {
    fontSize: 45,
    fontWeight: 10,
    fontFamily: "Neucha",
    color: "red",
  },

  inputs: {
    border: "2px solid #000",
    borderRadius: 5,
    marginBottom: 15,
    color: "blue",
    height: 40,
  },

  button: {
    height: 50,
    width: 20,
    marginTop: 20,
  },

  footer: {
    marginTop: 40,
    display: "block",
  },

  error: {
    color: "red",
  },
});
