import { StatusBar } from "expo-status-bar";
import React from "react";
import {
  SafeAreaView,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from "react-native";

export default function App() {
  return (
    <View style={styles.container}>
      <View>
        <Text> Manejo de varias Pantallas</Text>
      </View>
      <View>
        <TouchableOpacity style={{ backgroundColor: "gray" }}>
          <Text style={{fontWeight:'bold', textAlign:'c'}}>Contar</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
});
