import { StatusBar } from "expo-status-bar";
import React from "react";
import {
  SafeAreaView,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from "react-native";

export default function App() {
  return (
    <View style={styles.container}>
      <View>
        <Text> Manejo de varias Pantallas</Text>
      </View>
      <View style={{flexDirection:'row', backgroundColor:'red'}}>
        <TouchableOpacity style={{margin:5,  backgroundColor: "aqua", padding:5, borderRadius:5 }}>
          <Text style={{ fontWeight: "bold", textAlign: "center", color:'blue' }}>
            Contar
          </Text>
        </TouchableOpacity>
        <TouchableOpacity style={{margin:5, backgroundColor: "aqua", padding:5, borderRadius:5 }}>
          <Text style={{ fontWeight: "bold", textAlign: "center", color:'blue' }}>
            Descontar
          </Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  bu
});
