import { StatusBar } from "expo-status-bar";
import React from "react";
import { SafeAreaView, StyleSheet, Text, TextInput, View } from "react-native";

export default function App() {
  return (
    <SafeAreaView style={{ flex: 1 }}>
      <View style={{ height: 100, backgroundColor: "yellow" }}>
        <Text>Manejo de Layout y Varias Pantallas</Text>
      </View>
      <View style={{ flex: 1, height:100, backgroundColor: "coral", alignItems: 'center', justifyContent: 'center' }}>
        <Text>Cuerpo</Text>
        <TextInput
        placeholder= "Ingrse nombre"
        />
      </View>
      <View style={{ height:100, backgroundColor: "gray" }}></View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
});
