import { StatusBar } from "expo-status-bar";
import React from "react";
import { SafeAreaViewBase, StyleSheet, Text, View } from "react-native";

export default function App() {
  return (
    <SafeAreaViewBase style={{flex:1}}> // Una sola llave las clases. Doble llave para los estilos en el mismo componente.
    <View style={{height:100, backgroundColor:'yellow'}}>
      <Text>Manejo de Layout y Varias Pantallas</Text>
    </View>
    </SafeAreaViewBase>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
});
